package com.hunter.base.dao;import org.springframework.beans.factory.annotation.Autowired;import java.util.List;import java.util.Map;/** * @Author zhangcl * @Date 16/7/4  下午10:15 */public abstract  class BaseDao<T> implements IBaseDao{    @Autowired    protected MybatisBaseDao<T> mybatisBaseDao;    public long countBySql(String sql, Map param) {        return mybatisBaseDao.countBySql(sql,param);    }    public Object findOneById(long id) {        return mybatisBaseDao.findOneById(id);    }    public Object findOneBySql(String sql, Map param) {        return mybatisBaseDao.findOneBySql(sql,param);    }    public List findListByIds(List ids) {        return mybatisBaseDao.findListByIds(ids);    }    public List findListBySql(String sql, Map param) {        return mybatisBaseDao.findListBySql(sql,param);    }    public int insert(Object entity) {        return mybatisBaseDao.insert(entity);    }    public int updateBySql(String sql, Map param) {        return mybatisBaseDao.updateBySql(sql,param);    }    public int deleteById(long id) {        return mybatisBaseDao.deleteById(id);    }    public int deleteByIds(List ids) {        return mybatisBaseDao.deleteByIds(ids);    }    public int deleteBySql(String sql, Map param) {        return mybatisBaseDao.deleteBySql(sql,param);    }}