package com.hunter.base.dao;import java.util.List;import java.util.Map;/** * @Author zhangcl * @Date 16/7/4  下午10:43 */public interface IBaseDao<T> {    int insertOne(T entity);    void insertList(List<T> list);    long countBySql(String sql, Map<String, Object> param);    T findOneById(long id);    T findOneBySql(String sql, Map<String, Object> param);    List<T> findListBySql(String sql, Map<String, Object> param);    List<T> findListBySql(String sql);    int updateBySql(String sql, Map<String, Object> param);    void updateByEntity(T entity);    int deleteById(long id);    int deleteByIds(List<Long> ids);    int deleteBySql(String sql, Map<String, Object> param);}