package com.hunter.base.vo;import com.alibaba.fastjson.JSONObject;import lombok.Getter;import lombok.Setter;import lombok.ToString;import java.util.Map;/** * @Author zhangcl * @Date 17/5/24  上午8:47 */@Getter@Setter@ToStringpublic class ApiResponseVo {    private String code;    private String message;    private Map<String, Object> data;    private static final String SUCCESS = "success";    private static final String SUCCESS_CODE = "2000";    private ApiResponseVo(String code, String message, Map<String, Object> data) {        this.code = code;        this.message = message;        this.data = data;    }    public static String error(String code, String message) {        return error(code, message, null);    }    public static String error(String code, String message, Map<String, Object> data) {        ApiResponseVo vo = new ApiResponseVo(code, message, data);        String json = JSONObject.toJSONString(vo);        vo = null;        return json;    }    public static String success(Map<String, Object> data) {        ApiResponseVo vo = new ApiResponseVo(SUCCESS_CODE, SUCCESS, data);        String json = JSONObject.toJSONString(vo);        vo = null;        return json;    }}