package com.hunter.base.vo;import java.util.Map;/** * @Author zhangcl * @Date 17/5/4  上午8:32 */public class RequestData {    /**     * 开始的条数     */    private int start;    /**     * 每页的条数     */    private int length;    /**     * 请求次数     */    private int draw;    /**     * 数据     */    private Map<String, Object> data;    public int getStart() {        return start;    }    public void setStart(int start) {        this.start = start;    }    public int getLength() {        return length;    }    public void setLength(int length) {        this.length = length;    }    public int getDraw() {        return draw;    }    public void setDraw(int draw) {        this.draw = draw;    }    public Map<String, Object> getData() {        return data;    }    public void setData(Map<String, Object> data) {        this.data = data;    }    /**     * 得到页数     * @return     */    public int getPageNum() {        if (length > 0) {            return 1 + start / length;        }        return 0;    }    @Override    public String toString() {        return "RequestData{" +                "start=" + start +                ", length=" + length +                ", draw=" + draw +                ", data=" + data +                '}';    }}