package com.hunter.common.constant;/** * @Author zhangcl * @Date 16/7/10  下午3:21 */public enum OrmEnum {    mybatis(1, "mybatis"), jdbc(2, "jdbc"), hibernate(3, "hibernate"), mongodb(4, "mongodb");    private int index;    private String code;    OrmEnum(int index, String code) {        this.index = index;        this.code = code;    }    public int getIndex() {        return index;    }    public String getCode() {        return code;    }    public String getCodeByIndex(int index) {        for (OrmEnum ormEnum : OrmEnum.values()) {            if (ormEnum.getIndex() == index) {                return ormEnum.getCode();            }        }        return "";    }}