package com.hunter.common.constant;/** * @Author zhangcl * @Date 17/4/16  上午9:13 */public enum SexEnum {    man("男", "M"), female("女", "F"), other("其他", "O");    private String name;    private String code;    SexEnum(String name, String code) {        this.name = name;        this.code = code;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getCode() {        return code;    }    public void setCode(String code) {        this.code = code;    }    public String getCodeByName(String name) {        for (SexEnum sexEnum : SexEnum.values()) {            if (sexEnum.getName().equals(name)) {                return sexEnum.getCode();            }        }        return "";    }}