package com.hunter.common.util;import java.util.Random;/** * @Author zhangcl * @Date 17/5/17  下午9:29 */public class CodeGeneratorUtil {    public static final String YYYYMMDD="yyyy-MM-dd";    public static final String YYYYMMDDHHMMSS="yyyy-MM-dd HH:mm:ss";    public static final String YYYYMMDDHHMMSSMIS="yyyy-MM-dd HH:mm:ss";    private CodeGeneratorUtil() {    }    /**     * 生成指定范围内的随机数     *     * @param min 最小数值,包括此值     * @param max 最大数据,不包括此值     * @return     */    public static int getRandomInt(int min, int max) {        if (min < 0) {            throw new IllegalArgumentException("param min must great than 0");        }        if (max > Integer.MAX_VALUE) {            throw new IllegalArgumentException("param max must less than " + Integer.MAX_VALUE);        }        if (min > max) {            throw new IllegalArgumentException("param min mst less than param max");        }        Random random = new Random();        int minusNumber = max - min;        return random.nextInt(minusNumber) + min;    }    public static long getCurrentTimeByFormat(){        return 0;    }    public static void main(String[] args){        for(int i=0;i<100;i++){            System.out.println(getRandomInt(1,10));        }    }}