package com.hunter.common.util;import com.google.common.hash.Hasher;import com.google.common.hash.Hashing;import java.nio.charset.Charset;/** * @Author zhangcl * @Date 17/4/16  上午12:19 */public class EncryptUtil {    private EncryptUtil() {    }    public static String encodeByMD5(String data) {        Hasher hasher = Hashing.md5().newHasher();        hasher.putString(data, Charset.defaultCharset());        return hasher.hash().toString();    }    public static void main(String[] args){        System.out.println(EncryptUtil.encodeByMD5("zhchlo"));    }}