package com.hunter.sys.base.dao;import com.google.common.collect.Maps;import com.hunter.base.dao.BaseDao;import com.hunter.sys.base.entity.SysUser;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Map;/** * @Author zhangcl * @Date 16/7/9  下午6:07 */@Repositorypublic class SysUserDao extends BaseDao<SysUser> {    /**     * 根据code查找用户     *     * @param userCode     * @return     */    public SysUser findUserByUserCode(String userCode) {        Map<String, Object> map = Maps.newHashMap();        map.put("code", userCode);        List<SysUser> sysUsers = super.findListByMap(map);        if (sysUsers != null && sysUsers.size() > 0) {            return sysUsers.get(0);        }        return null;    }}