package com.hunter.sys.base.mapper;import com.hunter.sys.base.entity.SysUser;import org.apache.ibatis.annotations.ResultMap;import org.apache.ibatis.annotations.Select;import java.util.List;/** * @Author zhangcl * @Date 17/6/29  上午8:51 */public interface SysUserMapper {    /**     * 根据code查询人员     * @param code     * @return     */    @Select("select * from sys_user a where a.is_del=0 and a.code=#{code} and a.password=#{pwd}")    @ResultMap("SysUserDao.allResultMap")    List<SysUser> getUserByCode(String code, String pwd);}