package com.hunter.sys.base.service;import com.hunter.sys.base.dao.SysUserDao;import com.hunter.sys.base.entity.SysUser;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Map;/** * @Author zhangcl * @Date 16/7/9  下午6:06 */@Servicepublic class SysUserService {    @Autowired    private SysUserDao sysUserDao;    /**     * 保存用户     * @param sysUser     */    public void save(SysUser sysUser) {        sysUserDao.insertOne(sysUser);    }    /**     * 查找所有用户     * @return     */    public List<SysUser> findAllUsers() {        List<SysUser> list = sysUserDao.findAll();        return list;    }    /**     * 根据id查找用户     * @param id     * @return     */    public SysUser findUserById(long id) {        SysUser sysUser = sysUserDao.findOneById(id);        return sysUser;    }    /**     * 根据code查找用户     * @param userCode     * @return     */    public SysUser findUserByUserCode(String userCode) {        SysUser sysUser = sysUserDao.findUserByUserCode(userCode);        return sysUser;    }    /**     * 更新     * @param user     */    public void updateByEntity(SysUser user){        sysUserDao.updateByEntity(user);    }    /**     * 根据map查找用户     * @param map     * @return     */    public List<SysUser> findListByMap(Map<String,Object> map){        return sysUserDao.findListByMap(map);    }}