package com.hunter.sys.controller;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import javax.servlet.http.HttpServletRequest;/** * Created by zhangchaolong on 16/6/16. */@Controller@RequestMapping("/sys/loginController")public class LoginController {    /**     *     * @param request     * @param modelMap     * @return     * @throws Exception     */    @RequestMapping("/login")    public String login(HttpServletRequest request, ModelMap modelMap)            throws Exception {        String userName=request.getParameter("username");        String pwd=request.getParameter("password");        System.out.println(userName);        System.out.println(pwd);        return "login/main";    }}