package com.hunter.sys.controller;import com.hunter.sys.entity.SysMenu;import com.hunter.sys.service.SysMenuService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import javax.servlet.http.HttpServletRequest;import java.util.List;/** * @Author zhangcl * @Date 16/7/9  下午6:07 */@Controller@RequestMapping("/sys/menu")public class SysMenuController {    @Autowired    SysMenuService sysMenuService;    @RequestMapping(value = "/list")    public String list(HttpServletRequest request, ModelMap modelMap) {        List<SysMenu> sysMenuList = sysMenuService.getAllTopMenu();        String[] properties={"","name","url","createName","createTime","id",""};        StringBuilder resultDate=new StringBuilder("[");        for(SysMenu sysMenu:sysMenuList){            resultDate.append(sysMenu.getProperties(sysMenu,properties)).append(",");        }        resultDate.replace(resultDate.length()-1,resultDate.length(),"]");        modelMap.put("data",resultDate);        System.out.println(resultDate);        return "sys/menu/listMenu";    }}