package com.hunter.sys.controller;import com.hunter.sys.entity.SysUser;import com.hunter.sys.service.SysUserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import javax.servlet.http.HttpServletRequest;import java.util.List;/** * @Author zhangcl * @Date 16/7/9  下午6:07 */@Controller@RequestMapping("/sys/user")public class SysUserController {    @Autowired    private SysUserService sysUserService;    @RequestMapping(value = "/list", method = RequestMethod.GET)    public String list(HttpServletRequest request, ModelMap modelMap) {        List<SysUser> lists=sysUserService.findAllUsers();        System.out.println("条数:"+lists.size());        return "sys/user/listUser";    }    @RequestMapping(value = "/add", method = RequestMethod.GET)    public String add(HttpServletRequest request, ModelMap modelMap) {        return "sys/user/addUser";    }}