package com.hunter.sys.mapper;import com.hunter.sys.entity.SysMenu;import org.apache.ibatis.annotations.ResultMap;import org.apache.ibatis.annotations.Select;import java.util.List;/** * @Author zhangcl * @Date 17/5/9  上午6:22 */public interface SysMenuMapper {    @Select("select * from sys_menu a where a.is_del=0 and a.is_valid=1 and parent_id=0")    @ResultMap("com.hunter.sys.mapper.SysMenuMapper.allResultMap")    List<SysMenu> getAllTopMenu();}