package com.hunter.sys.service;import com.hunter.sys.dao.SysUserDao;import com.hunter.sys.entity.SysUser;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @Author zhangcl * @Date 16/7/9  下午6:06 */@Servicepublic class SysUserService {    @Autowired    private SysUserDao sysUserDao;    /**     * 保存用户     * @param sysUser     */    public void save(SysUser sysUser) {        sysUserDao.insertOne(sysUser);    }    public List<SysUser> findAllUsers() {        List<SysUser> list = (List<SysUser>) sysUserDao.findListBySql("findAll");        return list;    }    public SysUser findUserById(long id) {        SysUser sysUser = sysUserDao.findOneById(id);        return sysUser;    }}