package com.hunter.sys.service;import com.hunter.sys.dao.SysUserDao;import com.hunter.sys.entity.SysUserEntity;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @Author zhangcl * @Date 16/7/9  下午6:06 */@Servicepublic class SysUserService {    @Autowired    private SysUserDao sysUserDao;    public List<SysUserEntity> getAllUsers(){        List<SysUserEntity> list= (List<SysUserEntity>) sysUserDao.findListBySql("getAllUsers");        return list;    }    public SysUserEntity getUserById(long id){        SysUserEntity sysUser= sysUserDao.findOneById(id);        return sysUser;    }}